import * as C from './styles'
import { Item } from '../../types/Item'
// sempre que criar props especificas em typescript, precisa criar type pARA ESSAS props
// e espefica tudo que vai receber
type Props = {
  item: Item,
  onChange: (id: number, done: boolean) => void
}
export const ListItem = ({item, onChange}: Props) => {
  // const [isChecked, setIsChecked] = useState(item.done); //pode modificar.

  return (
    // done interligado ao checked 
    <C.Container done={item.done}> 
      <input
        type="checkbox" 
        checked={isChecked}
        onChange={e => onChange(item.id, e.target.checked)} // quando modificar valor do input vai salvar o valor atual(marcado/desmarcado)
      />
      <label>{item.name}</label>
    </C.Container>
  )
}
