import  { useState } from 'react'
import * as C from './App.styles'
import { Item } from './types/Item'
import { ListItem} from './components/ListItem'
import { AddArea } from './components/AddArea'
const App = () => {
  const [list, setList] = useState<Item[]>([
    {id: 1, name: 'Ir ao sacolão', done: false }, // objeto
    {id: 2, name: 'Ir ao mercado', done: true } 
  ]) // typescript consegue definir os padroes de valores
  return (
    <C.Container><C.Area><C.Header>Lista de Tarefas </C.Header>
      {/* Área de adicionar nova tarefa */}


      {list.map((item, index)=> (
        <ListItem key={index} item={item}/>
      ))}

    </C.Area></C.Container>
  );
}
export default App;
